l1 , l2 = [0, 33, 37, 6, 10, 44, 13, 47, 16, 18, 22, 25], [1, 38, 48, 8, 41, 7, 12, 47, 16, 40, 20, 23, 25]
func = lambda x,y: print('Количество элементов, присутствующих в обоих списках:',len(x & y),*(x & y),'\nКоличество элементов, присутствующих только в одном списке: ',len(x ^ y),*(x^y),'\nКоличество оставшихся элементов в list1 после извлечения элементов из list2: ',len(x - y),*(x - y),'\nКоличество оставшихся элементов в list2 после извлечения элементов из list1: ',len(y - x),*(y - x))
func(set(l1),set(l2))